        <hr />

        <div className="d-grid gap-2">
          <Button variant="primary" size="lg">
            Make Request
          </Button>
          <Button variant="secondary" size="lg">
            Cancel
          </Button>
        </div>
      </Form>
    </Container>
  );
}

export default CustomerCreateNewRequest;
