import React, { useState } from "react";
import { Container, Button, Form, Row, Col } from "react-bootstrap";

function PaymentMethod() {
  const [form, setForm] = useState({
    cardNumber: "",
    email: "",
    expiry: "",
    CVV: "",
    billing: "",
  });

  async function handleSubmit() {}

  function handleCardNumberChange(event) {
    setForm({ ...form, cardNumber: event.target.value });
  }

  function handleEmailChange(event) {
    setForm({ ...form, email: event.target.value });
  }

  function handleExpiryChange(event) {
    setForm({ ...form, expiry: event.target.value });
  }

  function handleCVVChange(event) {
    setForm({ ...form, expiry: event.target.value });
  }

  return (
    <Container className="py-5">
      <h1>Add Payment Method</h1>

      <hr />

      <Form>
        <Form.Group className="mb-3" controlId="formBasicCardNumber">
          <Form.Label>Card Number</Form.Label>
          <Form.Control type="text" placeholder="Enter card number" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicCardNumberInfo">
          <Row>
            <Col>
              <Form.Label>Expiry</Form.Label>
            </Col>
            <Col>
              <Form.Label>CVV</Form.Label>
            </Col>
          </Row>
          <Row>
            <Col>
              <Form.Control type="text" placeholder="MM/YYYY" />
            </Col>
            <Col>
              <Form.Control type="text" placeholder="Enter CVV" />
            </Col>
          </Row>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicRepeatPassword">
          <Form.Label>Billing Email</Form.Label>
          <Form.Control type="text" placeholder="Enter billing email" />
        </Form.Group>
      </Form>

      <hr />

      <div className="d-grid gap-2">
        <Button variant="primary" size="lg">
          Confirm
        </Button>
        <Button variant="cancel" size="lg">
          Cancel
        </Button>
      </div>
    </Container>
  );
}

export default PaymentMethod;
